var app=new Vue({el:"#app",data:{list_width:"0%",list_display:"hidden"},methods:{list_appear:function(t){"0%"===this.list_width?(this.list_width="100%",this.list_display="visible"):(this.list_width="0%",this.list_display="hidden")},Home:function(t){this.list_appear(),$("html,body").animate({scrollTop:"0px"},800)},About:function(t){this.list_appear(),$("html,body").animate({scrollTop:$(".introduction").offset().top},800)},Contact:function(t){this.list_appear(),$("html,body").animate({scrollTop:$(".last").offset().top},800)}}});